mapper.select:
select t.numchargerecordeid, t.vc2ordercode, t.numuserid, (SELECT vc2sdkuserid from lxzb_gameuser g where g.numuserid=t.numuserid) as vc2sdkuserid, s.vc2areacode, t.numrecommanduserid, (SELECT vc2sdkuserid from lxzb_gameuser g where g.numuserid=t.numrecommanduserid) as vc2sdkrecommanduserid, t.numchargenum , s.NUMUSERID as agent_id, (SELECT vc2sdkuserid from lxzb_gameuser g where g.numuserid=s.NUMUSERID) as vc2sdkagentuserid, t.datorder from lxzb_charge_record t,lxzb_gamerole r, lxzb_areaserver s where t.numgameroleid=r.numgameroleid and r.numareaserverid=s.numareaserverid
AND t.vc2ordercode = #{vc2ordercode}
AND t.numuserid = ( SELECT numuserid FROM lxzb_gameuser G WHERE G.vc2sdkuserid = #{vc2sdkuserid} )
AND t.datorder >= #{datorderStart}
AND t.datorder <= #{datorderEnd}
AND s.vc2areacode = #{vc2areacode}
order by t.datcreate desc
